import React from 'react';
import { connect } from 'react-redux';
import * as d3 from 'd3';

class Graph extends React.Component {
    constructor(props){
        super(props);
        this.handleRender = this.handleRender.bind(this);
    }


    handleRender(){
        const h = 500;
        const w = 500;

        const element = d3.select('#selector')
        element.append('svg').attr('width', w).attr('heigh', h)

        //Xaxis
        const xScale = d3.scaleLinear()
        xScale.domain([this.props.data.x[0], this.props.data.x[this.props.data.x.length -1]])
        xScale.range([0 - 10, 10+10])
        //Yaxis
        const yScale = d3.scaleLinear()
        yScale.domain([-1, 1])
        yScale.range([0 - 10, 10+10])

    }
    render(){
        return <div id='selector'>
            <button className={'btn btn-dark'}>Render</button>
        </div>
    }
}

const styles = {}

function mapStateToProps(state){
    return {
        data: state.data,
        min: state.min,
        max: state.max
    }
}

export default connect(mapStateToProps,null)(Graph);
