function NRpoly(params, x0){
    return 0
}
function NRexp(params, x0){
    return 0
}
function NRlog(params, x0){
    return 0
}
function NRpow(params, x0){
    return 0
}

function stringToNumber(s){
    if (s.match(/\./g)){
            if (s.match(/\./g).length == 1){
                let aux = s.split('.')
                return parseInt(aux[0].concat(aux[1])/(10**aux[1].length))
            }
            else{
                return 'MORE_DOTS'
            }
        }
        else{
            if (s.match(/\D/)){
                return 'CHARACTER'
            }
            else{
                return parseInt(s)
            }
        }
}


function newtonRaphson(model, fp, precision = 0.01){
    console.log('ANTES: ',fp)
    fp.min = stringToNumber(fp.min)
    fp.max = stringToNumber(fp.max)
    fp.p = fp.p.map(stringToNumber)
    console.log('DEPOIS: ',fp)
    const x0 = (fp.min +fp.max)/2
    switch(model){
        case 'POL':
            return NRpoly(fp.p,x0,precision)
        case 'EXP':
            return NRexp(fp.p,x0,precision)
        case 'POW':
            return NRpow(fp.p,x0,precision)
        case 'LOG':
            return NRlog(fp.p,x0,precision)
        default:
            return 'Set the parameters'
    }
}

export default newtonRaphson;
