import React from 'react';
import { connect } from 'react-redux';
import * as d3 from 'd3';

class Graph extends React.Component {
    constructor(props){
        super(props);
        this.handleRender = this.handleRender.bind(this);
    }



    handleRender(){
        if (!this.props.data.x){
            console.log('Please set the parameters')
        }
        else{
        let dataset = [];
        for (let i=0;i<this.props.data.x.length;i++){
            dataset.push([this.props.data.x[i],this.props.data.y[i]])
        }
        console.log(dataset)
        const h = 500;
        const w = 500;
        const padding = 60;

        const element = d3.select('#selector').append('svg').attr('width', w).attr('height', h)


        //Xaxis
        const xScale = d3.scaleLinear()
        xScale.domain([0, this.props.data.x[this.props.data.x.length -1]])
        xScale.range([this.props.data.x[0] - 10,  this.props.data.x[this.props.data.x.length -1] +10 ])
        //Yaxis
        const yScale = d3.scaleLinear()
        yScale.domain([this.props.data.y[0], this.props.data.x[this.props.data.y.length -1]])
        yScale.range([this.props.data.y[0] - 10, this.props.data.x[this.props.data.y.length -1]+10])

        //plot data
        element.selectAll("circle")
        .data(dataset)
        .enter()
        .append("circle")
        .attr("cx", d => d[0])
        .attr("cy", d => h - d[1])
        .attr("r", 5)

        const xAxis = d3.axisBottom(xScale)
        const yAxis = d3.axisBottom(yScale)

        element.append('g').attr('transform', `translate(0,${h})`).call(xAxis)
        element.append('g').attr('transform', 'translate(0, 0)').call(yAxis)        

        }

    }
    render(){
        return <div id='selector'>
            <button onClick={this.handleRender} className={'btn btn-dark'}>Render</button>
        </div>
    }
}

const styles = {}

function mapStateToProps(state){
    return {
        data: state.data,
    }
}

export default connect(mapStateToProps,null)(Graph);
